import { Component, OnInit } from '@angular/core';
import { KpiService } from '../../services/kpi.service';
import { KPI } from '../../models/kpi.model';
import { Chart } from 'chart.js/auto';

@Component({
  selector: 'app-kpi-overview',
  templateUrl: './kpi-overview.component.html',
  styleUrls: ['./kpi-overview.component.scss']
})
export class KpiOverviewComponent implements OnInit {
  showRawData: boolean = false;
  get rawKpiJson(): string {
    return JSON.stringify(this.kpis, null, 2);
  }
  kpis: KPI[] = [];
  selectedNWB: string = 'RBRO';
  selectedPeriod: string = '';
  deploymentChart: any;
  failureRateChart: any;

  get periods(): string[] {
    return [...new Set(this.kpis.map(kpi => `${kpi.year} ${kpi.quarter}`))];
  }

  get filteredKpis(): KPI[] {
    if (!this.selectedPeriod) return this.kpis;
    const [year, quarter] = this.selectedPeriod.split(' ');
    return this.kpis.filter(kpi => String(kpi.year) === year && String(kpi.quarter) === quarter);
  }

  get totalTeams(): number {
    // Unique teams with a non-empty name in the filtered set
    return new Set(this.filteredKpis.map(kpi => kpi.team).filter(t => t)).size;
  }

  // --- Sorting and Filtering State ---
  deploymentSortKey: string = 'value';
  deploymentSortDir: 'asc' | 'desc' = 'desc';
  deploymentFilter: string = '';

  cycleSortKey: string = 'value';
  cycleSortDir: 'asc' | 'desc' = 'desc';
  cycleFilter: string = '';

  mttrSortKey: string = 'value';
  mttrSortDir: 'asc' | 'desc' = 'desc';
  mttrFilter: string = '';

  testAutoSortKey: string = 'value';
  testAutoSortDir: 'asc' | 'desc' = 'desc';
  testAutoFilter: string = '';

  // --- Trend Table Getters (sorted & filtered) ---
  get deploymentFrequencyTrend() {
    let rows = this.filteredKpis.map(kpi => ({
      team: kpi.team,
      product: kpi.product,
      value: kpi.deploymentFrequency
    }));
    if (this.deploymentFilter) {
      rows = rows.filter(row =>
        (row.team?.toLowerCase().includes(this.deploymentFilter.toLowerCase()) ||
         row.product?.toLowerCase().includes(this.deploymentFilter.toLowerCase()))
      );
    }
    rows = rows.sort((a, b) => {
      const valA = a[this.deploymentSortKey];
      const valB = b[this.deploymentSortKey];
      if (valA == null) return 1;
      if (valB == null) return -1;
      if (valA < valB) return this.deploymentSortDir === 'asc' ? -1 : 1;
      if (valA > valB) return this.deploymentSortDir === 'asc' ? 1 : -1;
      return 0;
    });
    return rows;
  }

  get cycleTimeTrend() {
    let rows = this.filteredKpis.map(kpi => ({
      team: kpi.team,
      product: kpi.product,
      value: kpi.cycleTime
    }));
    if (this.cycleFilter) {
      rows = rows.filter(row =>
        (row.team?.toLowerCase().includes(this.cycleFilter.toLowerCase()) ||
         row.product?.toLowerCase().includes(this.cycleFilter.toLowerCase()))
      );
    }
    rows = rows.sort((a, b) => {
      const valA = a[this.cycleSortKey];
      const valB = b[this.cycleSortKey];
      if (valA == null) return 1;
      if (valB == null) return -1;
      if (valA < valB) return this.cycleSortDir === 'asc' ? -1 : 1;
      if (valA > valB) return this.cycleSortDir === 'asc' ? 1 : -1;
      return 0;
    });
    return rows;
  }

  get mttrTrend() {
    let rows = this.filteredKpis.map(kpi => ({
      team: kpi.team,
      product: kpi.product,
      value: kpi.mttr
    }));
    if (this.mttrFilter) {
      rows = rows.filter(row =>
        (row.team?.toLowerCase().includes(this.mttrFilter.toLowerCase()) ||
         row.product?.toLowerCase().includes(this.mttrFilter.toLowerCase()))
      );
    }
    rows = rows.sort((a, b) => {
      const valA = a[this.mttrSortKey];
      const valB = b[this.mttrSortKey];
      if (valA == null) return 1;
      if (valB == null) return -1;
      if (valA < valB) return this.mttrSortDir === 'asc' ? -1 : 1;
      if (valA > valB) return this.mttrSortDir === 'asc' ? 1 : -1;
      return 0;
    });
    return rows;
  }

  get testAutomationTrend() {
    let rows = this.filteredKpis.map(kpi => ({
      team: kpi.team,
      product: kpi.product,
      value: kpi.testAutomation
    }));
    if (this.testAutoFilter) {
      rows = rows.filter(row =>
        (row.team?.toLowerCase().includes(this.testAutoFilter.toLowerCase()) ||
         row.product?.toLowerCase().includes(this.testAutoFilter.toLowerCase()))
      );
    }
    rows = rows.sort((a, b) => {
      const valA = a[this.testAutoSortKey];
      const valB = b[this.testAutoSortKey];
      if (valA == null) return 1;
      if (valB == null) return -1;
      if (valA < valB) return this.testAutoSortDir === 'asc' ? -1 : 1;
      if (valA > valB) return this.testAutoSortDir === 'asc' ? 1 : -1;
      return 0;
    });
    return rows;
  }

  // --- Methods to update sort and filter ---
  setDeploymentSort(key: string) {
    if (this.deploymentSortKey === key) {
      this.deploymentSortDir = this.deploymentSortDir === 'asc' ? 'desc' : 'asc';
    } else {
      this.deploymentSortKey = key;
      this.deploymentSortDir = 'desc';
    }
  }
  setCycleSort(key: string) {
    if (this.cycleSortKey === key) {
      this.cycleSortDir = this.cycleSortDir === 'asc' ? 'desc' : 'asc';
    } else {
      this.cycleSortKey = key;
      this.cycleSortDir = 'desc';
    }
  }
  setMttrSort(key: string) {
    if (this.mttrSortKey === key) {
      this.mttrSortDir = this.mttrSortDir === 'asc' ? 'desc' : 'asc';
    } else {
      this.mttrSortKey = key;
      this.mttrSortDir = 'desc';
    }
  }
  setTestAutoSort(key: string) {
    if (this.testAutoSortKey === key) {
      this.testAutoSortDir = this.testAutoSortDir === 'asc' ? 'desc' : 'asc';
    } else {
      this.testAutoSortKey = key;
      this.testAutoSortDir = 'desc';
    }
  }

  constructor(private kpiService: KpiService) {}

  ngOnInit(): void {
    this.loadKPIs();
  }

  loadKPIs(): void {
    this.kpiService.getKPIsByNWB(this.selectedNWB).subscribe(data => {
      this.kpis = data;
      console.log('Fetched KPIs:', this.kpis);
      if (this.kpis.length > 0) {
        console.log('Sample KPI:', this.kpis[0]);
        console.log('Types:', {
          deploymentFrequency: typeof this.kpis[0].deploymentFrequency,
          changeFailureRate: typeof this.kpis[0].changeFailureRate
        });
      } else {
        console.log('No KPIs returned for NWB', this.selectedNWB);
      }
      this.updateCharts();
    });
  }

  updateCharts(): void {
    const quarters = [...new Set(this.kpis.map(kpi => `${kpi.quarter} ${kpi.year}`))];
    const deploymentData = quarters.map(q => {
      const qKpis = this.kpis.filter(kpi => `${kpi.quarter} ${kpi.year}` === q);
      return qKpis.reduce((sum, kpi) => sum + kpi.deploymentFrequency, 0) / qKpis.length;
    });

    const failureData = quarters.map(q => {
      const qKpis = this.kpis.filter(kpi => `${kpi.quarter} ${kpi.year}` === q);
      return qKpis.reduce((sum, kpi) => sum + kpi.changeFailureRate, 0) / qKpis.length;
    });

    this.createDeploymentChart(quarters, deploymentData);
    this.createFailureRateChart(quarters, failureData);
  }

  private createDeploymentChart(labels: string[], data: number[]): void {
    if (this.deploymentChart) {
      this.deploymentChart.destroy();
    }

    this.deploymentChart = new Chart('deploymentChart', {
      type: 'line',
      data: {
        labels,
        datasets: [{
          label: 'Deployment Frequency',
          data,
          borderColor: '#4CAF50',
          tension: 0.1
        }]
      },
      options: {
        responsive: true,
        scales: {
          y: {
            beginAtZero: true
          }
        }
      }
    });
  }

  private createFailureRateChart(labels: string[], data: number[]): void {
    if (this.failureRateChart) {
      this.failureRateChart.destroy();
    }

    this.failureRateChart = new Chart('failureRateChart', {
      type: 'line',
      data: {
        labels,
        datasets: [{
          label: 'Change Failure Rate',
          data,
          borderColor: '#F44336',
          tension: 0.1
        }]
      },
      options: {
        responsive: true,
        scales: {
          y: {
            beginAtZero: true,
            max: 100
          }
        }
      }
    });
  }

  onNWBChange(nwb: string): void {
    this.selectedNWB = nwb;
    this.loadKPIs();
  }
}
<div class="kpi-overview">
  <div class="header">
    <div class="summary-metrics" style="margin-bottom: 24px;">
      <h2>Summary</h2>
      <div>Total reporting teams for selected period: <strong>{{ totalTeams }}</strong></div>
    </div>

    <button (click)="showRawData = !showRawData" style="margin-bottom: 10px;">
      {{ showRawData ? 'Hide' : 'Show' }} Raw KPI Data
    </button>
    <div *ngIf="showRawData" style="max-height: 300px; overflow: auto; background: #f7f7f7; border: 1px solid #ccc; padding: 8px; margin-bottom: 20px;">
      <pre>{{ rawKpiJson }}</pre>
    </div>
    <h1>Engineering KPIs Overview</h1>
    <div class="nwb-selector">
      <label for="nwb">Network Bank:</label>
      <select id="nwb" [(ngModel)]="selectedNWB" (change)="onNWBChange(selectedNWB)">
        <option value="RBRO">RBRO</option>
        <option value="RBBH">RBBH</option>
      </select>
    </div>
  </div>

  <div class="charts-container">
    <div class="chart-wrapper">
      <h2>Deployment Frequency</h2>
      <canvas id="deploymentChart"></canvas>
    </div>
    <div class="chart-wrapper">
      <h2>Change Failure Rate</h2>
      <canvas id="failureRateChart"></canvas>
    </div>
  </div>

  <div class="kpi-metrics">
    <div class="metric-card" *ngFor="let kpi of kpis | slice:-1">
      <h3>Latest Metrics</h3>
      <div class="metric">
        <span>CI/CD Pipeline Coverage:</span>
        <span>{{kpi.cicdPipeline}}%</span>
      </div>
      <div class="metric">
        <span>Test Automation Coverage:</span>
        <span>{{kpi.testAutomation}}%</span>
      </div>
      <div class="metric">
        <span>MTTR:</span>
        <span>{{kpi.mttr}} hours</span>
      </div>
      <div class="metric">
        <span>DevOps Score:</span>
        <span>{{kpi.devOpsScore}}</span>
      </div>
    </div>
  </div>
</div>

<!-- Trend Tables -->
<div class="trend-tables" style="margin-top: 32px;">
  <h2>Trends by Team & Product</h2>
  <div class="trend-table-wrapper" style="display: flex; flex-wrap: wrap; gap: 32px;">
    <div class="trend-table" style="flex:1 1 300px; min-width: 260px;">
      <h3>Deployment Frequency</h3>
      <table style="width:100%; border-collapse: collapse;">
        <thead>
          <tr><th>Team</th><th>Product</th><th>Value</th></tr>
        </thead>
        <tbody>
          <tr *ngFor="let row of deploymentFrequencyTrend">
            <td>{{row.team}}</td>
            <td>{{row.product}}</td>
            <td>{{row.value}}</td>
          </tr>
        </tbody>
      </table>
    </div>
    <div class="trend-table" style="flex:1 1 300px; min-width: 260px;">
      <h3>Cycle Time</h3>
      <table style="width:100%; border-collapse: collapse;">
        <thead>
          <tr><th>Team</th><th>Product</th><th>Value</th></tr>
        </thead>
        <tbody>
